CREATE TABLE "SHOWS" (
    "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY, 
    "NAME" NVARCHAR2(100) NOT NULL,
    "ADDRESS_ID" NUMBER NOT NULL,
    "PRICE" NUMBER(19,2) NOT NULL,
    "SHOW_DATE" DATE NOT NULL, 
    "TICKET_CAPACITY" NUMBER(7,0) NOT NULL,      
    "SOLD_TICKETS" NUMBER(7,0) NOT NULL, 
    CONSTRAINT "PK_SHOW_ID" PRIMARY KEY ("ID"),
    CONSTRAINT "FK_ADDRESS_ID" FOREIGN KEY ("ADDRESS_ID") REFERENCES "ADDRESSES"
);